css:
:root {
  --main-background-color: #ffffff;
  --grid-color: #6e6e6e;
  --weekdays-bg-color: #ffffff;
  --weekdays-text-color: #3e3e3e;
  --day-bg-color: #f9f9f9;
  --today-text-color: #f53030;
  --now-marker-color: #f53030;
  --event-bg-color: #e2ddff;
  --event-text-color: #353535;
  --event-description-text-color: #bbb0ff;
  --daily-event-bg-color: #8174d4;
  --daily-event-text-color: #ffffff;
  --multi-day-event-bg-color: #695ac6;
  --multi-day-event-text-color: #ffffff;
  --no-events-bg-color: #695ac6;
  --no-events-text-color: #ffffff;
  font-size: 16px;
}

#content {
  overflow: hidden;
  height: 100%;
  background-color: var(--main-background-color);
}

ol, li {
  padding: 0;
  margin: 0;
  list-style: none;
}

.calendar-schedule {
  position: relative;
  height: 100%;
}

.day-container {
  display: flex;
  background-color: var(--day-bg-color);
  border-bottom: 1px solid var(--grid-color);
}

.title-container {
  width: 8rem;
  padding: 0.25rem;
  margin: 0.25rem;
  color: var(--weekdays-text-color);
  background-color: var(--weekdays-bg-color);
  border-radius: 4px;
}

.day-title-date, .day-title-month {
  display: inline-block;
}

.day-title-date {
  font-size: 1.5rem;
  font-weight: bold;
  margin: 0 0.25rem;
}

.day-container.today .day-title-date {
  color: var(--today-text-color);
  font-size: 1.8rem;
}

.day-title-weekday {
  font-size: 0.8rem;
  margin-left: 0.25rem;
}

.day-events {
  width: calc(100% - 9rem);
  padding: 0  0.25rem 0.25rem 0;
}

.calendar-event {
  color: var(--event-text-color);
  background-color: var(--event-bg-color);
  padding: 0.25rem;
  min-height: 1.75rem;
  top: 2rem;
  display: flex;
  align-items: center;
  flex-wrap: wrap;
  border-radius: 4px;
  margin: 0.3rem 0;
}

.calendar-event.all-day {
  color: var(--daily-event-text-color);
  background-color: var(--daily-event-bg-color);
}

.calendar-event.multi {
  color: var(--multi-day-event-text-color);
  background-color: var(--multi-day-event-bg-color);
}

.event-description {
  font-size: 0.9em;
  opacity: 0.8;
  margin-top: 0.2rem;
  flex-basis: 100%;
  width: 0;
  margin-left: 0.6rem;
}

.event-summary {
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
  margin-left: 0.3rem;
}

.event-start-time, .event-end-time {
  font-weight: bold;
}

.now-marker {
  background-color: var(--now-marker-color);
  height: 0.2rem;
  width: calc(100% + 1rem);
  margin: 0.4rem 0 0.4rem -1rem;
  position: relative;
}

.now-marker::before {
  content: '';
  width: 1rem;
  height: 1rem;
  background-color: var(--now-marker-color);
  top: -0.4rem;
  left: -0.5rem;
  position: absolute;
  border-radius: 50%;
}

.no-events-message {
  width: 100%;
  padding: 2rem 0;
  text-align: center;
  font-size: 2rem;
  font-weight: bold;
  background-color: var(--no-events-bg-color);;
  color: var(--no-events-text-color);
  border-bottom: 0.5rem solid var(--no-events-text-color);
}
