css:
body {
  width: 640px !important;
 height: 360px !important;
 text-align: center;
    font-family: sans-serif;
    font-weight: 100;
    }
#clockdiv {
    font-family: sans-serif;
    color: #fff;
    display: inline-block;
    font-weight: 100;
    text-align: center;
    font-size: 178px;
    }
#clockdiv > div {
    padding: 10px 15px;
    border-radius: 4px;
    background: #00BF96;
    display: inline-block;
    width: 250px;
 margin: 10px 0;
 }
#clockdiv div > span {
text-align: center;
    padding: 15px 0;
    width: 240px;
    border-radius: 4px;
    background: #00816A;
    display: inline-block;
    }
.smalltext {
    padding-top: 5px;
    font-size: 64px;
    }
.warning > #clockdiv > div {
    background: darkred;
    }
.warning > #clockdiv > div > span {
    background: red;
    }
