css:
body {
 width: 820px !important;
 height: 420px !important;
 font-family: "Helvetica", "Arial", sans-serif;
 line-height: 1;
}

.container-main {
 height: 420px !important;
 width: 820px !important;
}

.container {
 height: 420px !important;
 width: 820px !important;
 float: left;
 }

.row-finance {
 height: 60px;
 background: rgba(0, 0, 0, 0.87);
 margin-bottom: 20px;
}

.row-header {
 margin-right: -15px;
 margin-left: -15px;
 margin-bottom: 20px;
}

.header-rate {
 margin-left: 70%;
 width: 30%;
 text-align: center;
}

.value {
 font-size: 20px;
 padding-top: 20px;
 font-weight: bold;
 color: #fff;
}

.img-circle {
 border-radius: 50%;
 vertical-align: middle;
 border: 0;
}
.center-block {
 width: 50px;
 height: 50px;
 display: block;
 margin-right: auto;
 margin-left: auto;
}

.down-arrow {
 font-size: 20px;
 color: red;
 padding-top: 17px;
}

.up-arrow {
 font-size: 20px;
 color: green;
 padding-top: 17px;
}

.variant {
 font-size: 20px;
 padding-top: 17px;
}

.flags {
 padding-top: 4px;
 width: 15%;
 float: left;
}

.name {
 width: 30%;
 float: left;
}

.price {
 float: right;
 width: 30%;
 text-align: center;
}

.center-block {
 width: 50px;
 height: 50px;
}
