css:
* {
    -webkit-box-sizing: border-box;
    -moz-box-sizing: border-box;
    box-sizing: border-box
}

:after,:before {
    -webkit-box-sizing: border-box;
    -moz-box-sizing: border-box;
    box-sizing: border-box
}

.container {
    padding-right: 15px;
    padding-left: 15px;
    margin-right: auto;
    margin-left: auto;
}

.container-row {
    padding: 30px;
    margin-bottom: 20px;
    display: table;
    width: 100%;
}

.text-center {
    text-align: center;
}

body {
    width: 960px !important;
 height: 340px !important;
 font-family: "Helvetica", "Arial", sans-serif;
    line-height: 1;
}

.shadowed {
    text-shadow: 0px 0px 2px rgba(255, 255, 255, 0.5);
    filter: dropshadow(color=rgba(255, 255, 255, 0.5), offx=2, offy=2);
}

.container {
    background-repeat: no-repeat;
    background-size: cover;
    background-position: center;
    width: 960px !important;
    height: 340px !important;
}

.main-panel {
  height: 137px;
}

.current-temp {
    font-family: "Helvetica", sans-serif;
    font-weight: bold;
    font-size: 100px;
    color: #fff;
}

.main-icon {
    font-size: 70px;
    color: #fff;
}

.big-day {
    font-size: 30px;
    font-style: italic;
    font-weight: lighter;
    color: #272625;
}

.big-icon {
    font-size: 90px;
    padding-bottom: 30px;
    color: #272625;
}

.big-temp {
    padding: 10px 0px;
    font-size: 45px;
    color: #272625;
}

.small-day {
    font-size: 20px;
    font-style: italic;
    font-weight: lighter;
    color: #272625;
}

.small-icon {
    font-size: 30px;
    padding: 50px 0px 10px 0px;
    color: #272625;
}

.small-temp {
    padding: 10px 0px;
    font-size: 16px;
    color: #272625;
}

.powered {
    text-align: center;
    color: #272625;
}

.details-box {
    height: 220px;
    padding-left: 30px;
    color: #272625;
    width: 20%;
    display: inline-block;
}

.upcoming-days {
    height: 195px;
    margin-top: 20px !important;
    text-align: center;
    width: 20%;
    display: inline-block;
}

.selector-day {
    font-size: 15px;
    font-weight: light;
    margin-top: 25px;
}

.selector-temp {
    font-size: 25px;
    font-weight: bold;
    font-style: italic;
}
