css:
body {
    font-family: "Helvetica", "Arial", sans-serif;
    line-height: 1;
}

.shadowed {
    text-shadow: 0px 0px 2px rgba(255, 255, 255, 0.5);
    filter: dropshadow(color=rgba(255, 255, 255, 0.5), offx=2, offy=2);
}

.container {
    background-position: center;
    width: 340px !important;
    height: 200px !important;
}

.main-panel {
    margin-top: 10px;
    height: 137px;
}

.current-temp {
    font-family: "Helvetica", sans-serif;
    font-weight: bold;
    font-size: 100px;
    color: #fff;
}

.powered {
    padding-top: 5px;
}


.container-row {
    height: 290px;
    color: #fff;
    padding: 30px;
    position: relative;
    margin-bottom: 20px;
}

.big-container {
    display: inline-block;
    width: 50%;
}

.big-day {
    font-size: 30px;
    font-style: italic;
    font-weight: lighter;
    color: #272625;
}

.big-icon {
    font-size: 90px;
    color: #272625;
}

.big-temp {
    padding: 5px 0 0px 0;
    font-size: 25px;
    color: #272625;
}

.description {
    padding: 20px 0 0px 0;
    font-size: 45px;
    color: #272625;
}

.details-box {
    height: 290px;
    color: #272625;
    display: table;
    width: 100%;
}
