css:
body {
    font-family: "Helvetica", "Arial", sans-serif;
    line-height: 1;
}

.container {
    background-repeat: no-repeat;
    background-size: cover;
    background-position: center;
    width: 540px !important;
    height: 540px !important;
}

.main-panel {
    margin-top: 74px;
    height: 137px;
}

.big-icon {
    font-size: 200px;
    color: #fff;
}

.date {
    color: #fff;
    font-size: 18px;
    font-family: "Helvetica", sans-serif;
    font-weight: light;
    padding-bottom: 1px;
}

.month {
    font-size: 15px;
    color: #fff;
    font-family: "Helvetica", sans-serif;
    font-weight: light;
}

.current-temp {
    font-family: "Helvetica", sans-serif;
    font-weight: bold;
    font-size: 100px;
    color: #fff;
}

.main-cont {
    width: 400px;
    color: #fff;
    padding: 90px 50px;
    position: relative;
}

.main-icon {
    font-size: 70px;
    color: #fff;
}

.summary {
    font-weight: bold;
    font-size: 30px;
    padding-top: 20px;
}

.powered {
    font-weight: light;
    font-size: 15px;
    padding-bottom: 40px;
}

.small-day {
    padding-top: 50px;
    font-size: 28px;
    font-style: italic;
    font-weight: lighter;
    color: #fff;
}

.small-icon {
    font-size: 130px;
    padding: 20px 0px 40px 0px;
    color: #fff;
}

.big-temp {
    padding-top: 20px;
    font-size: 120px;
    color: #fff;
    font-weight: bold;
}

.even {
    background-color: rgba(0, 0, 0, 0.6);
    background: rgba(0, 0, 0, 0.6);
    color: #fff;
}

.odd {
    background-color: rgba(0, 0, 0, 0.8);
    background: rgba(0, 0, 0, 0.8);
    color: #fff !important;
}

.details-box {
    height: 290px;
    padding-left: 30px;
    color: #272625;
}

.upcoming-days {
    height: 365px;
    text-align: center;
    color: #fff;
}

.selector-day {
    font-size: 15px;
    font-weight: light;
    margin-top: 25px;
    color: #fff;
}

.selector-temp {
    font-size: 25px;
    font-weight: bold;
    font-style: italic;
}

.arrow-down {
    width: 0;
    height: 0;
    border-left: 20px solid transparent;
    border-right: 20px solid transparent;
    border-top: 20px solid rgba(0, 0, 0, 0.8);
    margin-left: 120px;
}

.row-details {
    margin-top: 128px;
    background-color: #fff;
    height: 182px;
    color: #fff;
}

.row-day {
    padding: 30px 0px;
    border-bottom: #eee solid 1px;
    color: #000;
}

.day {
    font-size: 30px;
    font-weight: bold;
    color: #434343;
}

.temp {
    font-size: 30px;
    font-weight: light;
    color: #434343;
}

.icon-up {
    font-size: 30px;
    color: color: #434343;
}

.row-time {
    padding: 20px 0px 10px 0px;
    color: #fff;
    font-weight: bold;
    font-size: 20px;
}

.shadowed {
    text-shadow: 0px 0px 2px #111;
    filter: dropshadow(color=#111, offx=2, offy=2);
}

.bg-cloudy {
    background-image: url('[cloudy-image]');
}

.bg-partly-cloudy-day {
    background-image: url('[day-cloudy-image]');
}

.bg-clear-day {
    background-image: url('[day-sunny-image]');
}

.bg-fog {
    background-image: url('[fog-image]');
}

.bg-sleet {
    background-image: url('[hail-image]');
}

.bg-clear-night {
    background-image: url('[night-clear-image]');
}

.bg-partly-cloudy-night {
    background-image: url('[night-partly-cloudy-image]');
}

.bg-rain {
    background-image: url('[rain-image]');
}

.bg-snow {
    background-image: url('[snow-image]');
}

.bg-wind {
    background-image: url('[windy-image]');
}
