css:
h1.MenuBoardName {
text-align: center;
font-size: 5em;
}

div.MenuBoardContainer {
font-family:"Trebuchet MS", Arial, Helvetica, sans-serif;
width:100%;
box-shadow: 0 1px 8px 8px rgba(0, 0, 0, 0.2);
font-size: 24px;
color: #3b3b3b;
margin-right: 10px;
}

.MenuBoardCategoryContainer {
font-size: 2em;
font-weight: 900;
color: #222;
text-align: center;
}

.MenuBoardMedia img {
max-width: 100%;
max-height: 100px;
display: block;
margin-left: auto;
margin-right: auto;
}

.MenuBoardMedia.MenuBoardProductMedia {
clear:both;
float:right;
}

.MenuBoardProductContainer {
width: 100%;
overflow: hidden;
margin-bottom:10px;
}

.MenuBoardProductContainer .MenuBoardProductName {
float: left;
font-size: 1.5em;
}

.MenuBoardProductContainer .MenuBoardProductPrice {
float: right;
font-size: 1.5em;
margin-right:4px;
}

.MenuBoardProductContainer .MenuBoardProductOptions {
clear:both;float: 
right;
font-size: 1.3em;
margin-right:4px;
}

.MenuBoardProductContainer .MenuBoardProductDescription {
float:left;
font-size: 1.2em;
}

.MenuBoardProductContainer .MenuBoardProductAllergyInfo {
clear:both;
float:left;
font-size: 1.2em;
}

.MenuBoardProductContainer.product-unavailable {
color:#f11313;
}

.MenuBoardProductContainer.product-highlight {
color:green;
font-size:48px;
}

.page.cycle-slide {
width:100%;
}
