css:
body {
  background-color: [background-color];
}

 #content, .menu-board-parent-container {
  position: relative;
 width: 1080px;
height: 1920px;
}

.menu-board-parent-container {
  display: flex;
  flex-direction: row;
}

.menu-board-name {
  color: [header-font-color];
  background: [primary-color];
  font-weight: bold;
  font-size: 3rem;
  writing-mode: vertical-lr;
 text-align: left;
  padding: 30px 20px 30px 20px;
}

.menu-board-categories-container {
  flex-basis: 100%;
  color: [font-color];
 margin: 1rem;
 border: 0.5rem solid [secondary-color];
}

.menu-board-zone {
  font-size: 1.4rem;
  padding: 0 1rem;
}

.menu-board-category-header {
 border-radius: 0 25px 25px 0;
 font-size: 2.2rem;
  background-color: [primary-color];
 color: [header-font-color];
 padding: 0.4rem 1rem;
  font-weight: bold;
 margin: 1rem -0.5rem 0.5rem -1rem;
}

.menu-board-categories-container .menu-board-zone:nth-child(2n-1) .menu-board-category-header {
    margin: 1rem -1rem 0.5rem -0.5rem;
    border-radius: 25px 0 0 25px;
}

.menu-board-product {
  margin: 1.6rem 0;
  display: grid;
  grid-template: "f a b""f c b""f d d""f e e" / 0fr 1fr 1fr;
  height: 100px;
  align-content: start;
}

.menu-board-product-page {
  width: 100%;
}

.menu-board-product-name {
  grid-area: a;
  font-size: 1.5rem;
  font-weight: bold;
}

.menu-board-product-price {
  grid-area: b;
  font-size: 1.6rem;
  text-align: right;
font-weight: bold;
}

.menu-board-product-description {
  grid-area: d;
  font-size: 1.2rem;
  text-overflow: ellipsis;
  overflow: hidden;
  white-space: nowrap;
 padding-bottom: 0.25rem;
 min-height: 1.2rem;
}

.menu-board-product-allergy {
  grid-area: c;
  color: [secondary-color];
}

.menu-board-product-options-container {
  grid-area: e;}

.menu-board-product-options {
  font-size: 1rem;
  color: [font-color];
 display: inline-block;
 margin-right: 2rem;
}

.menu-board-product.product-unavailable .menu-board-product-name, .menu-board-product.product-unavailable .menu-board-product-price, .menu-board-product.product-unavailable .menu-board-product-description, .menu-board-product.product-unavailable .menu-board-product-allergy, .menu-board-product.product-unavailable .menu-board-product-options {
  color: [disabled-color];
}

.menu-board-product.product-highlight .menu-board-product-name, .menu-board-product.product-highlight .menu-board-product-price {
  font-size: 1.8rem;
 color: [highlight-color];
 font-weight: bold;
}

.menu-board-product-image {
  grid-area: f; width: 120px;
  height: 100px;
 margin-right: 0.5rem;
 object-fit: cover;
  object-position: center;
}
