css:
#content, .menu-board-parent-container {
  position: relative;
 width: 1920px;
height: 1080px;
}

.menu-board-parent-container {
  background: [background-color];
}

.menu-board-name {
 position: absolute;
 color: [header-font-color];
 background: [header-color];
 border-radius: 1rem 1rem 0 0;
 font-weight: bold;
 font-size: 3rem;
 text-align: center;
 height: 10%;
 left: calc(calc(100% / 3) + 14px);
 bottom: 0;
 width: calc(calc(100% / 3) - 28px);
 display: flex;
 align-content: center;
 flex-direction: row;
 justify-content: center;
 align-items: center;
}

.menu-board-categories-container {
  height: 100%;
  color: [font-color];
}

.menu-board-zone {
  font-size: 1.4rem;
  padding: 0.8rem;
  border-style: solid;
  border-color: [primary-color];
  border-width: 0 0.3rem;
 display: flex;
 flex-direction: column;
 }

.menu-board-category-header {
  font-size: 0;
  color: [secondary-color];
 margin: -0.3rem;
 display: flex;
 flex-direction: column-reverse;
}

.menu-board-category-header-name {
 font-size: 2rem;
 margin: 0.2rem;
  font-weight: bold;
 border-bottom: 6px solid [secondary-color];
 padding: 0.4rem 0;
 }

.menu-board-category-image {
  width: 100%;
  height: 6rem;
  border-radius: 0rem 2rem 0 0;
 object-fit: cover;
  object-position: center;
}

#menuBoardZone_2 .menu-board-category-image {
 height: 9.5rem;
}

.menu-board-product {
  margin: 1.5rem 0;
  display: grid;
  grid-template: "f a b""f c b""f d d""f e e" / 0fr 1fr 1fr;
  height: 100px;
  align-content: start;
}

.menu-board-product-page {
  width: 100%;
}

.menu-board-product-name {
  grid-area: a;
  font-size: 1.5rem;
  font-weight: bold;
}

.menu-board-product-image {
  grid-area: f; width: 120px;
  height: 100px;
 margin-right: 0.5rem;
 object-fit: cover;
  object-position: center;
}

.menu-board-product-price {
  grid-area: b;
  font-size: 1.6rem;
  text-align: right;
}

.menu-board-product-description {
  grid-area: d;
  font-size: 1.2rem;
  text-overflow: ellipsis;
  overflow: hidden;
  white-space: nowrap;
 padding-bottom: 0.25rem;
 min-height: 1.2rem;
}

.menu-board-product-allergy {
  grid-area: c;
  color: [secondary-color];
}

.menu-board-product-options-container {
  grid-area: e;}

.menu-board-product-options { font-size: 1rem;
  color: [primary-color]; display: inline-block;
 margin-right: 2rem;
}

.menu-board-product.product-unavailable .menu-board-product-name, .menu-board-product.product-unavailable .menu-board-product-price, .menu-board-product.product-unavailable .menu-board-product-description, .menu-board-product.product-unavailable .menu-board-product-allergy, .menu-board-product.product-unavailable .menu-board-product-options {
  color: [disabled-color];
}

.menu-board-product.product-highlight .menu-board-product-name, .menu-board-product.product-highlight .menu-board-product-price {
  font-size: 1.8rem;
 color: [highlight-color];
  font-weight: bold;
}
