css:
body {
  width: 1920px !important;
 height: 1080px !important;
 background-color: [background-color];
}

 #content, .menu-board-parent-container {
  position: relative;
 width: 1920px;
height: 1080px;
}

.menu-board-parent-container {
  display: flex;
  flex-direction: column-reverse;
}

.menu-board-name {
  color: [header-font-color];
  background: [primary-color];
  font-weight: bold;
  font-size: 3rem;
  text-align: left;
  padding: 20px 0px 20px 20px;
  border-left: 80px solid [secondary-color];
}

.menu-board-categories-container {
  overflow: hidden;
 flex-basis: 100%;
  color: [font-color];
}

.menu-board-zone {
  font-size: 1.4rem;
  border-style: solid;
  border-color: [secondary-color];
  border-width: 0 0.5rem;
}

.menu-board-zone:last-of-type {
  border-right: none;
}

.menu-board-zone:first-of-type {
  border-left: none;
}

.menu-board-category-header {
  font-size: 2.5rem;
  color: [background-color];
 border-right: 10px solid [background-color];
 padding: 0.5rem;
 font-weight: bold;
 background-color: [secondary-color];
 }

.menu-board-products-container{
 margin: 0 1rem;
 }

.menu-board-product {
  margin: 1rem 0 1.2rem 0;
  display: flex;
 flex-direction: row;
 flex-wrap: wrap;
 align-content: flex-start;
 justify-content: space-between;
  height: 110px;
}

.menu-board-product-page {
  width: 100%;
}

.menu-board-product-name {
 order: 1;
 flex-basis: 60%;
 font-size: 1.5rem;
  font-weight: bold;
}

.menu-board-product-price {
 order: 2;
 flex-basis: 20%;
 font-size: 1.6rem;
  text-align: right;
}

.menu-board-product-description {
 order: 3;
 flex-basis: 100%;
 max-height: 2.4rem;
 font-size: 1.2rem;
 overflow: hidden;
 padding-bottom: 0.25rem;
 min-height: 1.2rem;
}

.menu-board-product-allergy {
 order: 4;

 width: 100%;
 line-height: 1rem;
 color: [secondary-color];
}

.menu-board-product-options-container {
 order: 5;
 width: 100%;}

.menu-board-product-options { font-size: 1rem;
  color: [primary-color]; display: inline-block;
 margin-right: 2rem;
}

.menu-board-product.product-unavailable .menu-board-product-name, .menu-board-product.product-unavailable .menu-board-product-price, .menu-board-product.product-unavailable .menu-board-product-description, .menu-board-product.product-unavailable .menu-board-product-allergy, .menu-board-product.product-unavailable .menu-board-product-options {
  color: [disabled-color];
}

.menu-board-product.product-highlight .menu-board-product-name, .menu-board-product.product-highlight .menu-board-product-price {
  font-size: 1.8rem;
 color: [highlight-color];
  font-weight: bold;
}

.menu-board-product-image {
 order: 0;
 flex: 2;
 margin-right: 1rem;
 width: 110px;
 height: 110px;
 object-fit: cover;
 }

 .menu-board-product-info {
 display: flex;
 flex-direction: row;
 flex-wrap: wrap;
 align-content: flex-start;
 justify-content: space-between;
 flex: 8;
}
