css:
.emojione {
    width: 26px;
    height: 26px
}

.item {
    float: left;
}

.tweet {
    height: 130px;
    padding: 10px;
}

.twitter-container {
    width: 1000px !important;
    height: 900px !important;
    font-size: 25px;
    line-height: 1.3;
    font-family: lucida sans unicode, lucida grande, sans-serif;
    padding-top: 10px;
}

div.profileimage {
    width: 90px;
    display: inline-block;
    vertical-align: top;
    position: relative;
    overflow: hidden;
    margin-left: 10px;
}

div.profileimage img {
    width: 90px;
    -webkit-border-radius: 50%;
    -moz-border-radius: 50%;
    -ms-border-radius: 50%;
    -o-border-radius: 50%;
    border-radius: 50%;
    border: 5px solid #ecf0f1;
    position: relative;
}

div.message-container {
    width: 870px;
    height: inherit;
    display: block;
    position: relative;
    padding-left: 20px;
    vertical-align: top;
    display: inline-block;
}

.message-container .user {
    height: 30px;
    font-weight: 700;
    display: inline-block;
    margin-bottom: .2em;
    color: #95a5a6;
    padding: 10px;
}

.message-container .date {
    padding: 10px;
    float: right;
    height: 30px;
}

.message-container .photo {
    text-align: center;
    height: auto;
    max-height: 690px;
    overflow: hidden;
}

.message-container .photo img {
    width: 100%;
    height: auto !important;
    vertical-align: middle;
}

.message {
    width: 100%;
    padding: 10px;
    background-color: #ecf0f1;
    position: relative;
    -webkit-border-radius: 8px;
    -moz-border-radius: 8px;
    -ms-border-radius: 8px;
    -o-border-radius: 8px;
    border-radius: 8px;
    text-align: left;
    display: inline-block;
    max-height: 880px;
}
