css:
.item {
    float: left;
}

body {
    font-family: "Helvetica", "Arial", sans-serif;
    line-height: 1;
    background-repeat: no-repeat;
    background-size: cover;
    background-position: center;
}

.twitter-container {
    padding-top: 30px;
    width: 600px !important;
    height: 460px !important;
    padding-bottom: 40px;
    background-color: rgba(255, 255, 255, 0.6);
    background: rgba(255, 255, 255, 0.6);
    color: rgba(255, 255, 255, 0.6);
}

.img-container img {
    width: 80px;
    height: 80px;
    border: 3px solid #fff;
}

.main-tweet {
    font-size: 35px;
    padding-top: 30px;
    line-height: 130%;
    color: #434343;
    height: 280px;
}

.emojione {
    width: 30px;
    height: 30px
}

.twitter-date, .twitter-location {
    margin-top: 30px;
    font-size: 17px;
    color: #434343;
    font-weight: bold;
}

.name {
    font-size: 25px;
    font-weight: bold;
    color: #434343;
    margin-top: 5px;
}

.username {
    margin-top: 5px;
    color: #434343;
}

.avatar {
    border: 3px solid #fff;
}
