css:
.item {
    float: left;
}

body {
    font-family: "Helvetica", "Arial", sans-serif;
    line-height: 1;
    background-repeat: no-repeat;
    background-size: cover;
    background-position: center;
}

.twitter-container {
    padding-top: 30px;
    width: 600px !important;
    height: 460px !important;
    padding-bottom: 40px;
    background-color: rgba(255, 255, 255, 0.6);
    background: rgba(255, 255, 255, 0.6);
    color: rgba(255, 255, 255, 0.6);
}

.img-container img {
    width: 80px;
    height: 80px;
    border: 3px solid #fff;
}

.main-tweet {
    font-size: 30px;
    padding-top: 20px;
    overflow: hidden;
    line-height: 130%;
    color: #434343;
    height: 280px;
}

.emojione {
    width: 30px;
    height: 30px
}

.twitter-date, .twitter-location {
    margin-top: 30px;
    font-size: 17px;
    color: #434343;
    font-weight: bold;
}

.name {
    font-size: 25px;
    font-weight: bold;
    color: #434343;
    margin-top: 5px;
}

.username {
    margin-top: 5px;
    color: #434343;
}

.avatar {
    border: 3px solid #fff;
}

.row {
    margin-right: -15px;
    margin-left: -15px;
}

.col-2 {
    width: 16.66666667%;
}

.col-3 {
    width: 25%;
}

.col-9 {
    width: 75%;
}

.col-10 {
    width: 83.33333333%;
}

.col-12 {
    width: 100%;
}

.twitter-col {
    float: left;
    position: relative;
    min-height: 1px;
    padding-right: 15px;
    padding-left: 15px;
}
