body {
    font-family: "Helvetica", "Arial", sans-serif;
    line-height: 1;
}

.twit {
    overflow: hidden;
    height: 160px !important;
    width: 400px !important;
    float: left;
    border: 1px solid #ddd;
    background: #FFF;
}

.icon {
  margin-top: 4px;
}

.main-tweet {
    font-size: 14px;
    padding: 16px 10px 8px 10px;
    line-height: 120%;
    color: #434343;
    text-align: justify;
}

.header {
    background: #1da1f2;
    width: 100%;
    height: 35px;
    margin-bottom: -2px;
}

.user {
    font-size: 14px;
    font-weight: bold;
    color: #fff;
    margin-top: 12px;
    letter-spacing: 1px;
    padding-left: 0px;
}

.arrow_box {
    height: 12px;
    position: relative;
    background: #1da1f2;
}

.arrow_box:after, .arrow_box:before {
    top: 96%;
    left: 50%;
    border: solid;
    content: " ";
    height: 0;
    width: 0;
    position: absolute;
    pointer-events: none;
}

.arrow_box:after {
    border-color: rgba(136, 183, 213, 0);
    border-top-color: #1da1f2;
    border-width: 16px;
    margin-left: -16px;
}

.arrow_box:before {
    border-color: rgba(0, 0, 0, 0);
    border-top-color: #;
    border-width: 26px;
    margin-left: -26px;
}

.row {
    margin-right: -15px;
    margin-left: -15px;
}

.col-2 {
    width: 16.66666667%;
}

.col-8 {
    width: 66.66666667%;
}

.col-12 {
    width: 100%;
}

.twitter-col {
    float: left;
    position: relative;
    min-height: 1px;
    padding-right: 15px;
    padding-left: 15px;
}
