css:
.item {
    float: left;
}

.emojione {
    width: 10px;
    height: 10px
}

body {
    font-family: "Helvetica", "Arial", sans-serif;
    line-height: 1;
}

.image-div {
    overflow: hidden;
    height: 110px;
    padding: 0;
}

.image-div IMG {
    border-radius: 50%;
    position: absolute;
    width: 70px;
    top: 50%;
    left: 50%;
    transform: translate(-50%,-50%);
-webkit-transform: translate(-50%, -50%);
    border: 3px solid #FFF;
}

.twit {
    overflow: hidden;
    height: 650px !important;
    width: 350px !important;
    float: left;
    border: 1px solid #ddd;
    background: #FFF;
}

.main-tweet {
    font-size: 24px;
    padding: 24px 16px;
    line-height: 150%;
    color: #4d4d4d;
    height: 480px;
}

.header {
    background: #1da1f2;
    width: 100%;
    height: 110px;
    border: 1px solid #ddd;
}

.user {
    font-size: 20px;
    font-weight: bold;
    color: #fff;
    margin-top: 40px;
    letter-spacing: 1px;
}

.tiempo {
    font-weight: lighter;
    font-size: 20px;
    margin-top: 15px;
}

.row {
    margin-right: -15px;
    margin-left: -15px;
}

.offset-3 {
    margin-left: 25%;
}

.col-2 {
    width: 16.66666667%;
}

.col-3 {
    width: 25%;
}

.col-4 {
    width: 33.33333333%;
}

.col-5 {
    width: 41.66666667%;
}

.col-6 {
    width: 50%;
}

.col-8 {
    width: 66.66666667%;
}

.col-12 {
    width: 100%;
}

.twitter-col {
    float: left;
    position: relative;
    min-height: 1px;
    padding-right: 15px;
    padding-left: 15px;
}
