css:
#content {
    color: #f9f9f9;
}

.shadowed {
text-shadow: 0px 0px 6px rgba(0, 0, 0, 0.6);
    filter: dropshadow(color=rgba(0, 0, 0, 0.6), offx=6, offy=6);
}

.top-content {
 height: 110px;
}

.row-element-half {
    display: inline-block;
    width: 50%;
    vertical-align: top;
}

.date {
    font-family: 'Roboto', sans-serif;
    font-weight: 900;
    font-size: 20px;
    text-transform: uppercase;
    padding: 8px 0px 0px 8px;
}

.temp {
    font-family: 'Roboto', sans-serif;
    font-size: 35px;
    font-weight: 900;
    padding: 8px 0px 0px 8px;
}

.main-icon {
    line-height: 84px;
    font-size: 60px;
    text-align: right;
    padding: 8px;
}

.bg-footer {
    background-color: #111;
    background-color: #111a;
    padding: 8px;
    font-size: 13px;
}

.forecast-current {
    margin-bottom: 4px;
    font-size: 14px;
    overflow: hidden;
}

.footer-powered-by {
    color: #f9f9f9;
    background-color: #000;
    opacity: 1;
}
