css:
#content {
    color: #f9f9f9;
}

.shadowed {
text-shadow: 0px 0px 6px rgba(0, 0, 0, 0.6);
    filter: dropshadow(color=rgba(0, 0, 0, 0.6), offx=6, offy=6);
}

.date {
    font-family: 'Roboto', sans-serif;
    font-weight: 900;
    font-size: 20px;
    margin-top: 5px;
    text-transform: uppercase;
}

.temp {
    font-family: 'Roboto', sans-serif;
    font-size: 35px;
    font-weight: 900;
    padding: 10px 0;
}

.main-icon {
    font-size: 0;
    padding: 20px 0;
}

.main-icon i {
    font-size: 60px;
}

.bg-footer {
    background-color: #111;
    background-color: #111a;
    padding: 6px 0;
    font-size: 13px;
    margin: -5px -25px 0px -25px;
}

.forecast-current {
    margin-bottom: 4px;
    font-size: 14px;
    overflow: hidden;
}

.footer-powered-by {
    color: #f9f9f9;
    background-color: #000;
    opacity: 1;
}
