css:
#content {
    color: #fff;
}

.top-content {
    height: 75px;
}

.bottom-content, .middle-content {
text-align: center;
}

.shadowed {
text-shadow: 0px 0px 2px rgba(0, 0, 0, 0.4);
    filter: dropshadow(color=rgba(0, 0, 0, 0.4), offx=2, offy=2);
}

.date {
    font-family: 'Roboto', sans-serif;
    font-weight: 900;
    font-size: 20px;
    text-transform: uppercase;
    float: left;
    padding: 4px;
}

.temp {
    font-family: 'Roboto', sans-serif;
    font-size: 50px;
    font-weight: 900;
    line-height: 50px;
    float: right;
    text-align: right;
    padding: 4px;
}

.main-icon-container {
    text-align: center;
}

.main-icon {
    font-size: 0;
}

.main-icon i {
    font-size: 80px;
}

.bg-footer {
    position: relative;
    margin-top: 10px;
    background-color: #000;
    opacity: 0.75;
    padding: 5px 0;
    font-size: 14px;
}
