css:
#content {
    color: #fff;
}

.shadowed {
text-shadow: 0px 0px 2px rgba(0, 0, 0, 0.4);
    filter: dropshadow(color=rgba(0, 0, 0, 0.4), offx=2, offy=2);
}

.date {
    font-family: 'Roboto', sans-serif;
    font-weight: 900;
    font-size: 20px;
    text-transform: uppercase;
    padding-top: 10px;
    line-height: 20px;
}

.temp {
    font-family: 'Roboto', sans-serif;
    font-size: 43px;
    font-weight: 900;
    padding-top: 6px;
    line-height: 40px;
}

.main-icon {
    font-size: 0;
}

.main-icon i {
    font-size: 100px;
}

.bg-footer {
    position: relative;
    margin-top: 10px;
    background-color: #000;
    opacity: 0.75;
    padding: 5px 0;
}
