css:
#content {
    color: #fff;
}

.shadowed {
text-shadow: 0px 0px 2px rgba(0, 0, 0, 0.4);
    filter: dropshadow(color=rgba(0, 0, 0, 0.4), offx=2, offy=2);
}

.corner-info {
    width: 200px;
    background-color: #000;
    background-color: #0008;
    opacity: 0.6;
    border-radius: 0 0 30px 0;
    padding: 8px;
}

.block-container {
    display: inline-block;
    width: 50%;
    height: 50px;
    vertical-align: top;
}

.date {
    font-family: 'Roboto', sans-serif;
    font-weight: 900;
    font-size: 20px;
    text-transform: uppercase;
}

.temp {
    font-family: 'Roboto', sans-serif;
    font-size: 30px;
    font-weight: 900;
}

.main-icon {
    font-size: 0;
    padding: 8px;
}

.main-icon i {
    font-size: 50px;
    padding: 5px;
}

.footer-powered-by {
    color: #f9f9f9;
    font-size: 14px;
    background-color: #111;
    opacity: 0.5;
}
