css:
#content {
    color: #f9f9f9;
}

.shadowed {
text-shadow: 0px 0px 8px rgba(0, 0, 0, 0.6);
    filter: dropshadow(color=rgba(0, 0, 0, 0.6), offx=8, offy=8);
}

.row-element-half {
    display: inline-block;
    width: 50%;
    vertical-align: top;
}

.region-top {
    height: 120px;
}

.region-bottom {
    background-color: #111;
    background-color: #111a;
    border-radius: 0 12px 0 0;
    padding: 6px;
    bottom: 14px;
    font-size: 14px;
    width: 90%;
    height: 94px;
}

.region-middle {
    font-size: 24px;
    line-height: 24px;
    font-weight: bold;
    padding: 0 4px;
}

.main-icon {
    text-align: right;
    font-size: 50px;
    line-height: 78px;
    padding: 8px;
}

.temp {
    font-size: 60px;
    padding: 8px;
}

.forecast-current {
    margin-bottom: 12px;
    font-weight: bold;
    height: 40px;
}

.forecast-second {
    font-size: 12px;
}

.footer-powered-by {
    opacity: 0.9;
    color: #f9f9f9;
    background-color: #000;
}
