css:
.clockContainer {
  text-align: center;
  padding: 5px;
}

.hourText {
  font-size: 40px;
  color: #323232;
  line-height: 45px;
}

.world-clock-label {
  color: #a1a1a1;
  font-size: 14px;
  line-height: 15px;
}

.highlighted .hourText {
  font-weight: bold;
}

.clockContainer p {
  margin: 0;
}
