css:
body {
   background: #222;
 }

.world-clock {
  color: #f9f9f9;
  font-size: 14px;
}

.clockContainer {
  padding: 10px;
  text-align: left;
}

.world-clock-label {
  color: #2993c3;
  font-size: 16px;
}

.highlighted .world-clock-label {
  font-weight: bold;
  font-size: 20px;
}

.clockContainer p {
  margin: 0;
  line-height: 18px;
}
